result = false;
let countrySet = [];

// TRIGGER

if (Array.isArray(data.partniorDataGrid)) {
  for (let i = 0; i < data.partniorDataGrid.length; i++) {
    if (
      data.partniorDataGrid[i]["page37"] &&
      !isEmpty(data.partniorDataGrid[i]["page37"])
    ) {
      countrySet.push(data.partniorDataGrid[i]["page37"].toString());
    }
  }
}

// Check data.page13 values against countrySet and update if necessary
if (Array.isArray(data.page13)) {
  for (let j = 0; j < data.page13.length; j++) {
